let dataset = "5 3";
let parts = dataset.split(" ");
let n = parseFloat(parts[0]);
let k = parseFloat(parts[1]);

let total = 2;
let table = [0, total];

for (let i = 2; i <= n; i++) {
    let mature = table[i - 1] + table[i - 2];
    let pairs = Math.floor(mature / 2.0);
    let kids = pairs * k * 2;
    table.push(kids);
    total += kids;
}

console.log(table);
console.log(total / 2);